import React from 'react';

export interface DropDownItem {
    text: string;
    callback: () => void;
}

export interface DropDownProps {
    options: DropDownItem[];
}

export interface DropDownState {
    shouldShow: boolean;
}

class DropDown extends React.Component<DropDownProps, DropDownState> {
    constructor(props: DropDownProps) {
        super(props);
        this.showDropDown = this.showDropDown.bind(this);
        this.hideDropDown = this.hideDropDown.bind(this);
        this.state = {
            shouldShow: false,
        };
    }

    showDropDown(e: any) {
        e.preventDefault();

        this.setState({ shouldShow: true }, () => {
            document.addEventListener('click', this.hideDropDown);
        });

    }
    hideDropDown() {
        this.setState({ shouldShow: false }, () => {
            document.removeEventListener('click', this.hideDropDown);
            console.log(this.state.shouldShow);
        });
    }

    items(): JSX.Element[] {
        console.log(this.props.options);
        return this.props.options.map((option, index) => {
            return <button onClick={(e) => {e.stopPropagation(); option.callback()}} key={index} >{option.text}</button>;
        });
    }

    render() {
        return (
            <div className='dropDown-container'>
                <button onClick={(e) => {e.stopPropagation(); this.showDropDown(e)}}>Articles</button>
                {this.state.shouldShow ? <div>{this.items()}</div>  : <></>}
            </div>
        );
    }
}

export default DropDown;
