import React from 'react';

export interface DropDownProps {
    options: string[];
}

export interface DropDownState {
    shouldShow: boolean;
}

class DropDown extends React.Component<DropDownProps, DropDownState> {
    constructor(props: DropDownProps) {
        super(props);
        this.showDropDown = this.showDropDown.bind(this);
        this.hideDropDown = this.hideDropDown.bind(this);
        this.state = {
            shouldShow: false
        };
    }

    async showDropDown(e: any) {
        e.preventDefault();

        await this.setState({ shouldShow: true }, () => {
        document.addEventListener('click', this.hideDropDown);
        });

    }
    async hideDropDown() {
        await this.setState({ shouldShow: false }, () => {
        document.removeEventListener('click', this.hideDropDown);
        });
    }

    items(): JSX.Element[] {
        console.log(this.props.options);
        return this.props.options.map(option => {
            return <button key={option}>{option}</button>;
        });
    }

    render() {
        console.log(this.state.shouldShow);
        return (
            <div className='dropDown-container'>
                <button onClick={this.showDropDown}>Articles</button>
                {this.state.shouldShow ? <div>{this.items()}</div>  : <>hj</>}
            </div>
        );
    }
}

export default DropDown;
