import * as React from 'react';
import 'tinymce/tinymce';
import ContentManager from '../../../ContentManager'
import DropDown from '../../common/DropDown'
// import 'react-draft-wysiwyg/dist/react-draft-wysiwyg.css';
// import {EditorState} from 'draft-js';

import SunEditor from 'suneditor-react';
import SunEditorCore from 'suneditor/src/lib/core';
import 'suneditor/dist/css/suneditor.min.css'; // Import Sun Editor's CSS File
import {
    align,
    font,
    fontColor,
    fontSize,
    formatBlock,
    hiliteColor,
    horizontalRule,
    lineHeight,
    list,
    paragraphStyle,
    table,
    template,
    textStyle,
    image,
    link
} from 'suneditor/src/plugins';

export interface WriteArticleProps {}

export interface WriteArticleState {
    articleTitles : string[];
}

class WriteArticle extends React.Component<WriteArticleProps, WriteArticleState> {
    editorRef: React.RefObject<SunEditorCore> = React.createRef();

    constructor(props: WriteArticleProps) {
        super(props);
        this.state = {
            articleTitles: [],
        };
    }

    handleChange(content : string) {
        console.log("OnChange: ", content);
    }

    render() {

        let articleTitles : string[] = [];
        ContentManager.articles.forEach(article => {
            console.log(article.title);
            articleTitles.push(article.title);
        });

        return (
            <>
                <DropDown options={articleTitles}/>
                <SunEditor
                    autoFocus={true}
                    lang='en'
                    setOptions={{
                        showPathLabel: false,
                        minHeight: '50vh',
                        maxHeight: '50vh',
                        placeholder: 'Enter your text here',
                        plugins: [
                            align,
                            font,
                            fontColor,
                            fontSize,
                            formatBlock,
                            hiliteColor,
                            horizontalRule,
                            lineHeight,
                            list,
                            paragraphStyle,
                            table,
                            template,
                            textStyle,
                            image,
                            link
                        ],
                        buttonList: [
                            ['undo', 'redo'],
                            ['font', 'fontSize', 'formatBlock'],
                            ['paragraphStyle'],
                            ['bold', 'underline', 'italic', 'strike', 'subscript', 'superscript'],
                            ['fontColor', 'hiliteColor'],
                            ['removeFormat'],
                            "/", // Line break
                            ['outdent', 'indent'],
                            ['align', 'horizontalRule', 'list', 'lineHeight'],
                            ['table', 'link', 'image']
                        ],
                        formats: ['p', 'div', 'h1', 'h2', 'h3', 'h4', 'h5', 'h6'],
                        font: [
                            'Arial',
                            'Calibri',
                            'Comic Sans',
                            'Courier',
                            'Garamond',
                            'Georgia',
                            'Impact',
                            'Lucida Console',
                            'Palatino Linotype',
                            'Segoe UI',
                            'Tahoma',
                            'Times New Roman',
                            'Trebuchet MS'
                        ]
                    }} onChange={this.handleChange}
                />
            </>
        );
    }
}

export default WriteArticle;
